}
.toast{
  min-width:260px; max-width:360px; background:#111; color:#fff;
  border:1px solid rgba(255,255,255,.12); border-radius:10px;
  box-shadow:0 10px 30px rgba(0,0,0,.25);
  padding:12px 14px; display:flex; align-items:flex-start; gap:10px;
  pointer-events:auto;
  animation:toastIn .25s ease-out both;
}
.toast.success{ border-color:#2ecc71; }
.toast.error{ border-color:#e74c3c; }
.toast .title{ font-weight:700; margin-bottom:2px; font-size:14px; }
.toast .msg{ font-size:13px; color:#ddd; }
.toast .close{
  margin-left:auto; background:transparent; border:0; color:#fff; font-size:18px; line-height:1;
  cursor:pointer; opacity:.8;
}
.toast .close:hover{ opacity:1; }
@keyframes toastIn{ from{opacity:0; transform:translateY(-8px)} to{opacity:1; transform:translateY(0)} }
@keyframes toastOut{ to{opacity:0; transform:translateY(-8px)} }

