{{-- Vanilla JS for modals + toasts + AJAX forms --}}
<script>
document.addEventListener('DOMContentLoaded', () => {
  // === Toast helpers ===
  const toaster = document.getElementById('toast-container');
  function showToast({type='success', title='Success', message='', timeout=4500}){
    if(!toaster) return;
    const el = document.createElement('div');
    el.className = `toast ${type}`;
    el.innerHTML = `
      <div>
        <div class="title">${title}</div>
        <div class="msg">${message}</div>
      </div>
      <button class="close" aria-label="Dismiss">&times;</button>
    `;
    const closer = el.querySelector('.close');
    const remove = () => { el.style.animation = 'toastOut .18s ease-in both'; setTimeout(() => el.remove(), 160); };
    closer.addEventListener('click', remove);
    toaster.appendChild(el);
    if(timeout){ setTimeout(remove, timeout); }
  }

  // === Modal helpers ===
  function openModal(id){
    const m = document.getElementById(id);
    if(!m) return;
    m.style.display = 'flex';
    m.setAttribute('aria-hidden','false');
    requestAnimationFrame(() => {
      const f = m.querySelector('input,select,textarea,button');
      f && f.focus({preventScroll:true});
    });
  }
  function closeModal(id){
    const m = document.getElementById(id);
    if(!m) return;
    m.style.display = 'none';
    m.setAttribute('aria-hidden','true');
  }

  document.getElementById('popupTrigger')?.addEventListener('click', e => { e.preventDefault(); openModal('popupForm'); });
  document.getElementById('popupTrigger2')?.addEventListener('click', e => { e.preventDefault(); openModal('popupForm2'); });
  document.getElementById('popupTrigger3')?.addEventListener('click', e => { e.preventDefault(); openModal('popupForm3'); });

  document.getElementById('closePopup')?.addEventListener('click', () => closeModal('popupForm'));
  document.getElementById('closePopup2')?.addEventListener('click', () => closeModal('popupForm2'));
  document.getElementById('closePopup3')?.addEventListener('click', () => closeModal('popupForm3'));

  ['popupForm','popupForm2','popupForm3'].forEach(id => {
    const m = document.getElementById(id);
    m?.addEventListener('click', evt => { if(evt.target === m) closeModal(id); });
  });
  document.addEventListener('keydown', evt => {
    if(evt.key === 'Escape'){
      ['popupForm','popupForm2','popupForm3'].forEach(closeModal);
    }
  });

  // === Form helpers ===
  function clearErrors(form){
    form.querySelectorAll('.invalid-feedback.d-block').forEach(n => n.remove());
    form.querySelectorAll('.is-invalid').forEach(el => el.classList.remove('is-invalid'));
  }
  function renderErrors(form, errors){
    Object.entries(errors || {}).forEach(([name, msgs]) => {
      const field = form.querySelector(`[name="${CSS.escape(name)}"]`);
      if(field){
        field.classList.add('is-invalid');
        const div = document.createElement('div');
        div.className = 'invalid-feedback d-block';
        div.textContent = Array.isArray(msgs) ? msgs[0] : String(msgs);
        field.insertAdjacentElement('afterend', div);
      }
    });
  }

  function wireForm(formId, modalId){
    const form = document.getElementById(formId);
    if(!form) return;

    let sending = false;
    form.addEventListener('submit', async e => {
      e.preventDefault();
      if(sending) return;
      sending = true;

      clearErrors(form);

      const btn = form.querySelector('button[type="submit"]');
      const orig = btn?.textContent;
      if(btn){ btn.disabled = true; btn.textContent = 'Submitting!'; }

      try{
        const formData = new FormData(form);
        const token = form.querySelector('input[name="_token"]')?.value;
        const res = await fetch(form.action, {
          method: 'POST',
          headers: token
            ? {'X-CSRF-TOKEN': token, 'Accept':'application/json', 'X-Requested-With':'XMLHttpRequest'}
            : {'Accept':'application/json', 'X-Requested-With':'XMLHttpRequest'},
          body: formData,
          credentials: 'same-origin'
        });

        const isJson = res.headers.get('content-type')?.includes('application/json');
        const data = isJson ? await res.json() : {};

        if(res.ok){
          form.reset();
          modalId && closeModal(modalId);
          showToast({type:'success', title:'Message sent', message: data.message || 'Thanks! Weâ€™ll get back to you shortly.'});
        }else if(res.status === 422){
          renderErrors(form, data.errors || {});
          showToast({type:'error', title:'Please fix the errors', message: data.message || 'Some fields need your attention.'});
        }else if(res.status === 419){
          showToast({type:'error', title:'Session expired', message:'Please refresh the page and try again.'});
        }else{
          showToast({type:'error', title:'Submission failed', message: data.message || 'Please try again or contact us directly.'});
        }
      }catch(err){
        showToast({type:'error', title:'Network error', message:'Please check your connection and try again.'});
      }finally{
        if(btn){ btn.disabled = false; btn.textContent = orig; }
        sending = false;
      }
    });
  }

  wireForm('quoteForm',  'popupForm');
  wireForm('quoteForm2', 'popupForm2');
  wireForm('quoteForm3', 'popupForm3');
});
</script>

